components.template:
{{comment " + Test: templates/1.html " }}
<div class="monkey melt-1">monkey</div>
<div>leave me out of diz</div>
{{comment " - Test " }}
<div class="shop melt-Index">
  <form class="add melt-Index" hx-post="/add" hx-target="#items" hx-swap="outerHTML">
    <input id="item" name="item" type="text" class="melt-Index"/>
    <button type="submit" class="melt-Index">+</button>
  </form>
  {{template "items" .Items}}
</div>
{{define "items"}}
<div id="items" class="flex-col melt-Index">
  <h1 class="melt-Index">items</h1>
  {{range $Index_id, $Index_item := .}}
  <div class="item melt-Index">
    <div class="melt-Index">{{$Index_item}}</div>
    <div class="add melt-Index" hx-delete="/add?item={{$Index_id}}" hx-target="#items" hx-swap="outerHTML">(add)</div>
    <div class="delete melt-Index" hx-delete="/delete?item={{$Index_id}}" hx-target="#items" hx-swap="outerHTML">(delete)</div>
  </div>
  {{end}}
</div>
{{end}}
